describe("Accessibility Tests",function(){var a;var b;it("Should have the slider role",function(){a=$("#accessibilitySliderA").slider();b=$("#accessibilitySliderB").slider();var d=$(a.slider("getElement"));var c=$(b.slider("getElement"));expect(d.find(".min-slider-handle").attr("role")).toBe("slider");expect(c.find(".min-slider-handle").attr("role")).toBe("slider");expect(c.find(".max-slider-handle").attr("role")).toBe("slider");expect(d.find(".tooltip-main").attr("role")).toBe("presentation");expect(d.find(".tooltip-min").attr("role")).toBe("presentation");expect(d.find(".tooltip-max").attr("role")).toBe("presentation")});it("Should have an aria-labelledby attribute",function(){a=$("#accessibilitySliderA").slider();b=$("#accessibilitySliderB").slider();expect($(a.slider("getElement")).find(".min-slider-handle").attr("aria-labelledby")).toBe("accessibilitySliderLabelA");expect($(b.slider("getElement")).find(".min-slider-handle").attr("aria-labelledby")).toBe("accessibilitySliderLabelA");expect($(b.slider("getElement")).find(".max-slider-handle").attr("aria-labelledby")).toBe("accessibilitySliderLabelB")});it("Should have an aria-valuemax and aria-valuemin value",function(){a=$("#accessibilitySliderA").slider({min:5,max:10});b=$("#accessibilitySliderB").slider({min:5,max:10});var d=$(a.slider("getElement"));var c=$(b.slider("getElement"));expect(d.find(".min-slider-handle").attr("aria-valuemin")).toBe("5");expect(d.find(".min-slider-handle").attr("aria-valuemax")).toBe("10");expect(c.find(".min-slider-handle").attr("aria-valuemin")).toBe("5");expect(c.find(".min-slider-handle").attr("aria-valuemax")).toBe("10");expect(c.find(".max-slider-handle").attr("aria-valuemin")).toBe("5");expect(c.find(".max-slider-handle").attr("aria-valuemax")).toBe("10")});it("Should have an aria-valuenow with the current value",function(){a=$("#accessibilitySliderA").slider({min:5,value:7});b=$("#accessibilitySliderB").slider({min:5,value:[2,8]});var d=$(a.slider("getElement"));var c=$(b.slider("getElement"));expect(d.find(".min-slider-handle").attr("aria-valuenow")).toBe("7");expect(c.find(".min-slider-handle").attr("aria-valuenow")).toBe("5");expect(c.find(".max-slider-handle").attr("aria-valuenow")).toBe("8");a.slider("setValue",1);b.slider("setValue",[4,9]);expect(d.find(".min-slider-handle").attr("aria-valuenow")).toBe("5");expect(c.find(".min-slider-handle").attr("aria-valuenow")).toBe("5");expect(c.find(".max-slider-handle").attr("aria-valuenow")).toBe("9")});afterEach(function(){if(a){a.slider("destroy")}if(b){b.slider("destroy")}})});