describe("Focus Option Tests",function(){var b;var a=function(d,e){var c=document.createEvent("MouseEvents");c.initEvent("mousedown",true,true);c.pageX=e;c.pageY=e;d.dispatchEvent(c)};it("handle should not be focused after value change when 'focus' is false",function(){b=$("#testSlider1").slider({min:0,max:10,value:0,focus:false,id:"testSlider"});var c;$("#testSlider").find(".min-slider-handle").focus(function(){c=true});a($("#testSlider").find(".slider-track-high").get(0),1000);expect(c).toBe(undefined)});it("handle should be focused after value change when 'focus' is true",function(){b=$("#testSlider1").slider({min:0,max:10,value:0,focus:true,id:"testSlider"});var c;$("#testSlider").find(".min-slider-handle").focus(function(){c=true});a($("#testSlider").find(".slider-track-high").get(0),1000);expect(c).toBe(true)});afterEach(function(){if(b){b.slider("destroy");b=null}})});