describe("Orientation Tests",function(){var b;var a;var c;describe("Vertical",function(){beforeEach(function(){b=new Slider("#orientationSlider",{id:"orientationSliderId",orientation:"vertical",min:0,max:10,value:5});var e=document.querySelector("#orientationSliderId .slider-handle");var d=e.getBoundingClientRect();a=d.top;c=d.left});afterEach(function(){if(b){b.destroy()}});it("slides up when handle moves upwards",function(){var f=document.createEvent("MouseEvents");var e=c;var d=a-100;var g;f.initMouseEvent("mousedown",true,true,window,null,0,0,e,d,false,false,false,false,0,null);b.sliderElem.dispatchEvent(f);g=b.getValue();expect(g).toBeLessThan(5)});it("slides down when handle moves downwards",function(){var f=document.createEvent("MouseEvents");var e=c;var d=a+100;var g;f.initMouseEvent("mousedown",true,true,window,null,0,0,e,d,false,false,false,false,0,null);b.sliderElem.dispatchEvent(f);g=b.getValue();expect(g).toBeGreaterThan(5)})})});