describe("Resize Tests",function(){var a,b;afterEach(function(){if(a){a.slider("destroy");a=null;b=null}});describe("Tick Labels",function(){var d,c;beforeEach(function(){var e=[0,100,200,300,400];c={ticks:e,ticks_labels:["$0","$100","$200","$300","$400"]}});it("should resize the tick labels when horizontal",function(){d=$("#resizeSlider");a=d.slider(c);b=a.data("slider");$(".slider").width(210);b._resize();expect(d.siblings("div.slider").find(".slider-tick-label:eq(0)").width()).toBe(52);$(".slider").width(120);b._resize();expect(d.siblings("div.slider").find(".slider-tick-label:eq(0)").width()).toBe(30);$(".slider").width(900);b._resize();expect(d.siblings("div.slider").find(".slider-tick-label:eq(1)").width()).toBe(225);$(".slider").width(210);b._resize();expect(d.siblings("div.slider").find(".slider-tick-label:eq(0)").width()).toBe(52)});it("should resize the tick labels when vertical",function(){var e=$("#resizeSliderVertical");a=e.slider(c);b=a.data("slider");$(".slider").height(210);b._resize();expect(e.siblings("div.slider").find(".slider-tick-label:eq(0)").height()).toBe(52);$(".slider").height(120);b._resize();expect(e.siblings("div.slider").find(".slider-tick-label:eq(0)").height()).toBe(30);$(".slider").height(900);b._resize();expect(e.siblings("div.slider").find(".slider-tick-label:eq(1)").height()).toBe(225);$(".slider").height(210);b._resize();expect(e.siblings("div.slider").find(".slider-tick-label:eq(0)").height()).toBe(52)})})});