describe("Scrollable body test",function(){var b;var a;var c;describe("Vertical scrolled body",function(){beforeEach(function(){b=new Slider("#veryLowPositionedSlider",{id:"scrollTestSliderId",orientation:"vertical",min:0,max:20,value:10,step:1});document.body.scrollTop=2000;var e=document.querySelector("#scrollTestSliderId .slider-handle");var d=e.getBoundingClientRect();a=d.top;c=d.left});afterEach(function(){if(b){b.destroy()}document.body.scrollTop=0});it("slides up when handle moves upwards after scroll page down",function(){var f=document.createEvent("MouseEvents");var e=c;var d=a-50;var g;f.initMouseEvent("mousedown",true,true,window,null,0,0,e,d,false,false,false,false,0,null);b.sliderElem.dispatchEvent(f);g=b.getValue();expect(g).toEqual(4)});it("slides down when handle moves downwards after scroll page down",function(){var f=document.createEvent("MouseEvents");var e=c;var d=a+50;var g;f.initMouseEvent("mousedown",true,true,window,null,0,0,e,d,false,false,false,false,0,null);b.sliderElem.dispatchEvent(f);g=b.getValue();expect(g).toEqual(14)})})});