describe("Scrollable test",function(){var c;var b;var d;var a;describe("Vertical inside scrollable container",function(){beforeEach(function(){c=new Slider("#ex1",{id:"ex1Slider",orientation:"vertical",min:0,max:20,value:10,step:1});a=document.querySelector("#scrollable-div");a.scrollTop=145;var f=document.querySelector("#ex1Slider .slider-handle");var e=f.getBoundingClientRect();b=e.top;d=e.left});afterEach(function(){if(c){c.destroy()}});it("slides up when handle moves upwards inside scrollable element after scrolling",function(){var g=document.createEvent("MouseEvents");var f=d;var e=b-50;var h;g.initMouseEvent("mousedown",true,true,window,null,0,0,f,e,false,false,false,false,0,null);c.sliderElem.dispatchEvent(g);h=c.getValue();expect(h).toEqual(4)});it("slides down when handle moves downwards inside scrollable element after scrolling",function(){var g=document.createEvent("MouseEvents");var f=d;var e=b+50;var h;g.initMouseEvent("mousedown",true,true,window,null,0,0,f,e,false,false,false,false,0,null);c.sliderElem.dispatchEvent(g);h=c.getValue();expect(h).toEqual(14)})})});