describe("Tick Label Render Tests",function(){var c;var b;beforeEach(function(){c=$("#testSlider1").slider({id:"slider1",ticks:[0,1,2],ticks_labels:["x","y","z"],orientation:"horizontal"});b=$("#testSlider2").slider({id:"slider2",ticks:[0,1,2],ticks_labels:["x","y","z"],orientation:"vertical"})});afterEach(function(){c.slider("destroy");c=null;b.slider("destroy");b=null});function a(e){var f=e.toLowerCase()==="horizontal"?1:2;var g=e.toLowerCase()==="horizontal"?false:true;var d="#slider"+f;it("Tick labels are rendered - "+e,function(){expect($(d).length).toBe(1);var h=$(d+" .slider-tick-label").length;expect(h).toBe(3)});it("Tick labels render inside the slider's bounds"+e,function(){expect($(d).length).toBe(1);var k=$(d)[0].getBoundingClientRect();var l=$(d+" .slider-tick-label");for(var j=0;j<l.length;j++){var h=l[j].getBoundingClientRect();if(g){expect(h.left).toBeGreaterThan(k.left);expect(h.top+10>=k.top).toBeTruthy()}else{expect(h.top+10>=k.top).toBeTruthy();expect(h.width/2+h.left>=k.left).toBeTruthy()}}})}a("horizontal");a("vertical")});