describe("Slider with ticks tests",function(){var a;it("Should have the number of tick marks you specify",function(){a=$("#testSlider1").slider({ticks:[100,200,300,400,500]});var b=$("#testSlider1").siblings("div.slider").find(".slider-tick").length;expect(b).toBe(5)});it("Should be at the default positions",function(){a=$("#testSlider1").slider({ticks:[100,200,300,400,500]});$("#testSlider1").siblings("div.slider").find(".slider-tick").each(function(b){expect(this.style.left).toBe(100*b/4+"%")})});it("Should be at the positions you specify",function(){var b=[0,10,20,30,100];a=$("#testSlider1").slider({ticks:[100,200,300,400,500],ticks_positions:b});$("#testSlider1").siblings("div.slider").find(".slider-tick").each(function(c){expect(this.style.left).toBe(b[c]+"%")})});it("Should have the tick labels you specify",function(){var c=["$0","$100","$200","$300","$400"];a=$("#testSlider1").slider({ticks:[100,200,300,400,500],ticks_labels:c});var b=$("#testSlider1").siblings("div.slider").find(".slider-tick-label");expect(b.length).toBe(c.length);b.each(function(d){expect(this.innerHTML).toBe(c[d])})});it("Should overwrite the min/max values",function(){a=$("#testSlider1").slider({ticks:[100,200,300,400,500],min:15000,max:25000});expect(a.slider("getAttribute","min")).toBe(100);expect(a.slider("getAttribute","max")).toBe(500)});it("Should not snap to a tick within tick bounds when using the keyboard navigation",function(){a=$("#testSlider1").slider({ticks:[100,200,300,400,500],ticks_snap_bounds:30});var c=$("#testSlider1").siblings("div.slider:first").find(".slider-handle");c.focus();var d=document.createEvent("Events");d.initEvent("keydown",true,true);var e=0;c.on("keydown",function(){e++;var f=$("#testSlider1").slider("getValue");expect(f).toBe(100+e)});d.keyCode=d.which=39;for(var b=0;b<5;b++){c[0].dispatchEvent(d)}});it("Should show the correct tick marks as 'in-selection', according to the `selection` property",function(){var b={ticks:[100,200,300,400,500],value:250,selection:"after"},c=$("#testSlider1");a=c.slider(b);expect(c.siblings("div.slider").find(".in-selection").length).toBe(3);a.slider("destroy");b.selection="before";a=c.slider(b);expect(c.siblings("div.slider").find(".in-selection").length).toBe(2)});it("Should reverse the tick labels if `reversed` option is set to true",function(){var e=[100,200,300,400,500];var d=["$100","$200","$300","$400","$500"];a=$("#testSlider1").slider({id:"testSlider1Ref",ticks:e,ticks_labels:d,ticks_snap_bounds:30,reversed:true});var b=$("#testSlider1Ref .slider-tick-label-container").children(".slider-tick-label").map(function(){return $(this).text()}).toArray();var c=d.reverse();expect(b).toEqual(c)});it("Should reverse the tick labels if `reversed` option is set to true and `ticks_positions` is specified",function(){var e=[0,100,200,300,400];var d=["$0","$100","$200","$300","$400"];a=$("#testSlider1").slider({id:"testSlider1Ref",ticks:e,ticks_labels:d,ticks_positions:[0,30,70,90,100],ticks_snap_bounds:20,value:200,reversed:true});var b=$("#testSlider1Ref .slider-tick-label-container .slider-tick-label").sort(function(g,f){var i=$(g).position().left;var h=$(f).position().left;return i-h}).map(function(){return $(this).text()}).toArray();var c=d.reverse();expect(b).toEqual(c)});afterEach(function(){if(a){a.slider("destroy");a=null}})});