describe("'tooltip_position' Option tests",function(){var a;afterEach(function(){if(a){a.destroy();a=null}});describe("vertical slider tests",function(){it("should be aligned to the left of the handle if set to 'left'",function(){a=new Slider("#testSlider1",{min:0,max:10,value:5,tooltip_position:"left",orientation:"vertical"});var b=a.tooltip.classList.contains("left");expect(b).toBeTruthy();expect(a.tooltip.style.right).toBe("100%")});it("should be aligned to the right of the handle if set to 'right'",function(){a=new Slider("#testSlider1",{min:0,max:10,value:5,tooltip_position:"right",orientation:"vertical"});var b=a.tooltip.classList.contains("right");expect(b).toBeTruthy();expect(a.tooltip.style.left).toBe("100%")});it("should default to 'right' if tooltip_position set to 'top'",function(){a=new Slider("#testSlider1",{min:0,max:10,value:5,tooltip_position:"top",orientation:"vertical"});var b=a.tooltip.classList.contains("right");expect(b).toBeTruthy();expect(a.tooltip.style.left).toBe("100%")});it("should default to 'right' if tooltip_position set to 'bottom'",function(){a=new Slider("#testSlider1",{min:0,max:10,value:5,tooltip_position:"bottom",orientation:"vertical"});var b=a.tooltip.classList.contains("right");expect(b).toBeTruthy();expect(a.tooltip.style.left).toBe("100%")})});describe("horizontal slider tests",function(){it("should be aligned above the handle if set to 'top'",function(){a=new Slider("#testSlider1",{min:0,max:10,value:5,tooltip_position:"top",orientation:"horizontal"});var b=a.tooltip.classList.contains("top");expect(b).toBeTruthy();expect(a.tooltip.style.top).toBe("")});it("should be aligned below the handle if set to 'bottom'",function(){a=new Slider("#testSlider1",{min:0,max:10,value:5,tooltip_position:"bottom",orientation:"horizontal"});var b=a.tooltip.classList.contains("bottom");expect(b).toBeTruthy();expect(a.tooltip.style.top).toBe("22px")});it("should be aligned below the handle if set to 'bottom' for range",function(){a=new Slider("#testSlider1",{min:0,max:20,value:[0,10],range:true,tooltip_position:"bottom",orientation:"horizontal"});var b=a.tooltip_min.classList.contains("bottom");var c=a.tooltip_max.classList.contains("bottom");expect(b).toBeTruthy();expect(c).toBeTruthy();expect(a.tooltip_min.style.top).toBe("22px");expect(a.tooltip_max.style.top).toBe("22px")});it("should default to 'top' if tooltip_position set to 'left'",function(){a=new Slider("#testSlider1",{min:0,max:10,value:5,tooltip_position:"left",orientation:"horizontal"});var b=a.tooltip.classList.contains("top");expect(b).toBeTruthy();expect(a.tooltip.style.top).toBe("")});it("should default to 'top' if tooltip_position set to 'right'",function(){a=new Slider("#testSlider1",{min:0,max:10,value:5,tooltip_position:"right",orientation:"horizontal"});var b=a.tooltip.classList.contains("top");expect(b).toBeTruthy();expect(a.tooltip.style.top).toBe("")})})});