describe("'tooltip_split' Option tests",function(){var d,b="tooltipedSlider",g,c,a,e,f={id:b,value:[0,10],tooltip:"always"};describe("When 'tooltip_split' is false",function(){beforeEach(function(){d=$("#testSlider1").slider($.extend(f,{tooltip_split:false}));g=$("#"+b);c=g.find(".tooltip-main");a=g.find(".tooltip-min");e=g.find(".tooltip-max")});it("should have `tooltip-main` displayed with `in` class",function(){expect(c.css("display")).not.toBe("none");expect(c.hasClass("in")).toBeTruthy()});it("should have `tooltip-min, tooltip-max` not displayed",function(){expect(a.css("display")).toBe("none");expect(a.hasClass("in")).toBeFalsy();expect(e.css("display")).toBe("none");expect(e.hasClass("in")).toBeFalsy()})});describe("When 'tooltip_split' is true",function(){beforeEach(function(){d=$("#testSlider1").slider($.extend(f,{tooltip_split:true}));g=$("#"+b);c=g.find(".tooltip-main");a=g.find(".tooltip-min");e=g.find(".tooltip-max")});it("should have `tooltip-min, tooltip-max` displayed with `in` class",function(){expect(a.css("display")).not.toBe("none");expect(a.hasClass("in")).toBeTruthy();expect(e.css("display")).not.toBe("none");expect(e.hasClass("in")).toBeTruthy()});it("should have `tooltip-main` not displayed",function(){expect(c.css("display")).toBe("none");expect(c.hasClass("in")).toBeFalsy()});it("should be aligned above the handle on init if set to 'top'",function(){expect(a.hasClass("top")).toBeTruthy();expect(e.hasClass("top")).toBeTruthy()})});afterEach(function(){if(d){d.slider("destroy");d=null}})});